      <font-awesome-icon icon="sign-out-alt" /> LogOut
    </a>
  </div>

</template>

<script>
import { Form, Field, ErrorMessage } from "vee-validate";
//import AddRates from "@/components/AddRates"
//const AddRates = () => import("./components/AddRates.vue")
import * as yup from "yup";

export default {
  name: "Home",
  components: {
//    AddRates,
    Form,
    Field,
    ErrorMessage,
  },
  data() {

    let currentStep=0;
    let showStep=false;

    const schema = yup.object().shape({
      email: yup
          .string()
          .required("Email обязательное поле!")
          .email("Email is invalid!")
          .max(50, "Must be maximum 50 characters!"),
      password: yup
          .string()
          .required("Password обязателен!")
          .min(6, "Минимальное кол-во символов 6")
          .max(40, "Максимальное кол-во символов 40!"),
      rules: yup
          .string()
          .required("Обязательное поле")
    });
    const schema2 = yup.object().shape({
      username: yup
          .string()
          .required("Обязательное поле")
    });

    if(this.$store.state.auth.status.loggedIn){
        if(this.$store.state.auth.user.username === null){
          showStep=true;
          currentStep=1;

        } else if(this.$store.state.auth.user.username != null && this.$store.state.auth.user.rate === 0 ){
          showStep=true;
          //console.log(this.$store.state.auth.user.username);
          currentStep=2;
        }

    }




    return {
      username:'',
      showPass:false,
      successful: false,
      loading: false,
      message: "",
      schema,
      schema2,
      showStep,
      currentStep,
      selectedRate:'',
      selectManager:'',
    };
  },
  computed: {
    Rates(){
      let arr = {};
      for (let key of Object.keys(this.$store.state.rates)) {
        arr[key]={
          'id':this.$store.state.rates[key].id,
          'name':this.$store.state.rates[key].name,
          'price':this.$store.state.rates[key].price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " "),
          'props':JSON.parse(this.$store.state.rates[key].props),
        }
        //console.log(this.$store.state.rates[key]);
      }
      //console.log(arr);
      return  arr;

    },
    Marketologs(){
      /*let arr = {};
      for (let key of Object.keys(this.$store.state.marketologs)) {
        arr[key]={
          'id':this.$store.state.rates[key].id,
          'name':this.$store.state.rates[key].name,
          'price':this.$store.state.rates[key].price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " "),
          'props':JSON.parse(this.$store.state.rates[key].props),
        }
        //console.log(this.$store.state.rates[key]);
      }*/
      //console.log(arr);
      return  this.$store.state.marketologs;

    },
    loggedIn() {
      return this.$store.state.auth.status.loggedIn;
    },
    currentUser() {
      return this.$store.state.auth.user;
    },

  },
  mounted() {
    console.log(this.currentUser);
    if(this.currentUser!==null){
      if(this.currentUser.manager!=null){
        if(this.currentUser.rules == 1){
          //this.$router.push("/admin");
        } else {
          //this.$router.push("/dashboard");
        }
      }
    }
    if (this.loggedIn) {
      console.log()
      //this.$router.push("/profile");
    }
  },
  watch:{
    '$store.state.auth.user.status.loggedIn': function() {
      let authUser = JSON.parse(localStorage.getItem('user'));
      //console.log(authUser);
      if(authUser === null){
        this.$router.push("/");
      } else if(authUser.username === null){
        this.currentStep=1;
       }
    }
  },
  methods: {


    selectedManager(id){
      this.message = "";
      this.loading = true;

      let userStorage = JSON.parse(localStorage.getItem('user'));

      let user={
        'user_id':userStorage.id,
        'rate':this.selectedRate.id,
        'manager':id
      }


      this.$store.dispatch("auth/step2", user).then(
          () => {
            //dashboard
            this.$router.push('/dashboard');
          },
          (error) => {
            this.message =
                (error.response &&
                    error.response.data &&
                    error.response.data.message) ||
                error.message ||
                error.toString();
            this.successful = false;
            this.loading = false;
          }
      );




    },
    selecteRate(id){
      console.log(this.Rates);
      for (let k in this.Rates){
        if(this.Rates[k].id===id){
          this.selectedRate=this.Rates[k]

        }
      }


    },
    logOut() {
      this.$store.dispatch('auth/logout');
      this.$router.push('/login');
    },
    showPassBt(){
      this.showPass=!this.showPass;
    },
    handleRegister(user) {
      this.message = "";
      this.successful = false;
      this.loading = true;

      this.$store.dispatch("auth/register", user).then(
          (data) => {
            this.message = data.message;
            this.successful = true;
            this.loading = false;
          },
          (error) => {
            this.message =
                (error.response &&
                    error.response.data &&
                    error.response.data.message) ||
                error.message ||
                error.toString();
            this.successful = false;
            this.loading = false;
          }
      );
    },
    handleStep1(user) {
      this.message = "";
      this.loading = true;
      let userStorage = JSON.parse(localStorage.getItem('user'));
      user.id=userStorage.id;
      console.log(userStorage);
      this.$store.dispatch("auth/step1", user).then(
          (data) => {

            this.currentStep=2;
            this.showStep=true;
            this.message = data.message;
            this.successful = true;
            this.loading = false;
          },
          (error) => {
            this.message =
                (error.response &&
                    error.response.data &&
                    error.response.data.message) ||
                error.message ||
                error.toString();
            this.successful = false;
            this.loading = false;
          }
      );
    }
  },
};
</script>

